mapper.insert:
INSERT INTO quest_reply(
	questreply_title, questreply_content, questreply_viewcount, questreply_regdate, user_no, filename_org, filename_real
) VALUES (
	#{questreply_title}, #{questreply_content}, 0, NOW(), #{user_no}, #{filename_org}, #{filename_real}
)

	SELECT LAST_INSERT_ID()


INSERT INTO quest_reply (
		questreply_title, questreply_content, questreply_viewcount, questreply_regdate, user_no, filename_org, filename_real, questreply_gno, questreply_ono, questreply_nested
) VALUES (
#{questreply_title}, #{questreply_content}, 0, NOW(),  #{user_no}, #{filename_org}, #{filename_real}, #{questreply_gno}, #{questreply_ono}, #{questreply_nested}
)

mapper.update:
UPDATE quest_reply SET
	questreply_title = #{questreply_title}
	,questreply_viewcount = #{questreply_viewcount}

	,filename_org = #{filename_org} 
	,filename_real = #{filename_real}

WHERE questreply_no = #{questreply_no}
