mapper.update:
UPDATE 
	reservable 
SET
	reservable_do = #{reservable_do} 
WHERE
	reservable_no = #{reservable_no}

mapper.insert:
INSERT INTO 
	reservable_major(
		reservable_no,
		reservable_major
	) VALUES (
		#{reservable_no},
		#{reservable_major}
	)

mapper.update:
