	reservable_major = #{reservable_major} 
WHERE
	reservable_major_no = #{reservable_major_no}

UPDATE 
	reservation
SET  
	reserve_pay = #{reserve_pay}
WHERE reserve_no = #{reserve_no}

mapper.insert:
INSERT INTO 
